sqlmap.listtotal:

select count(rc.CiId) as total from ResCi rc LEFT JOIN ResTypeDefine ResTypeDefine on rc.CiTypeid=ResTypeDefine.ResTypeID  
 left join ResAsset ResAsset on ResAsset.ConfigurationItemId=rc.CiId 
 LEFT JOIN ResAssetType ResAssetType ON ResAsset.AssetTypeId = ResAssetType.ID   #{left-join2}  
 #{where}
